{"filter":false,"title":"application.py","tooltip":"/eb-flask/application.py","undoManager":{"mark":0,"position":0,"stack":[[{"start":{"row":0,"column":0},"end":{"row":33,"column":21},"action":"insert","lines":["from flask import Flask","","# print a nice greeting.","def say_hello(username = \"World\"):","    return '<p>Hello %s!</p>\\n' % username","","# some bits of text for the page.","header_text = '''","    <html>\\n<head> <title>EB Flask Test</title> </head>\\n<body>'''","instructions = '''","    <p><em>Hint</em>: This is a RESTful web service! Append a username","    to the URL (for example: <code>/Thelonious</code>) to say hello to","    someone specific.</p>\\n'''","home_link = '<p><a href=\"/\">Back</a></p>\\n'","footer_text = '</body>\\n</html>'","","# EB looks for an 'application' callable by default.","application = Flask(__name__)","","# add a rule for the index page.","application.add_url_rule('/', 'index', (lambda: header_text +","    say_hello() + instructions + footer_text))","","# add a rule when the page is accessed with a name appended to the site","# URL.","application.add_url_rule('/<username>', 'hello', (lambda username:","    header_text + say_hello(username) + home_link + footer_text))","","# run the app.","if __name__ == \"__main__\":","    # Setting debug to True enables debug output. This line should be","    # removed before deploying a production app.","    application.debug = True","    application.run()"],"id":1}]]},"ace":{"folds":[],"scrolltop":196,"scrollleft":0,"selection":{"start":{"row":33,"column":21},"end":{"row":33,"column":21},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":{"row":13,"state":"start","mode":"ace/mode/python"}},"timestamp":1638557464334,"hash":"31971f8d14b13db7132d0c22ae0078a71eef01b0"}